DROP DATABASE IF EXISTS `vmall`;
CREATE DATABASE `vmall` /*!40100 DEFAULT CHARACTER SET utf8 */;
USE `vmall`;

DROP TABLE IF EXISTS `store`;
CREATE TABLE `store` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(255) DEFAULT NULL,
	`cid` int(11) DEFAULT NULL,
	`type` VARCHAR(255) DEFAULT NULL,
	`applyDate` datetime DEFAULT NULL,
	`addDate` datetime DEFAULT NULL,
	`applyName` VARCHAR(255) DEFAULT NULL,
	`mobile` VARCHAR(255) DEFAULT NULL,
	`telephone` VARCHAR(255) DEFAULT NULL,
	`identification` VARCHAR(255) DEFAULT NULL,
	`email` VARCHAR(255) DEFAULT NULL,
	`authentication_credit` int(2) DEFAULT NULL,
	`authentication_id` int(2) DEFAULT NULL,
	`authentication_phone` int(2) DEFAULT NULL,
	`summary_html` MEDIUMTEXT DEFAULT NULL,	
	`status` int(2) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=84 DEFAULT CHARSET=utf8;


DROP TABLE IF EXISTS `category`;
CREATE TABLE `category` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(255) DEFAULT NULL,
	`sid` int(11) NOT NULL DEFAULT 0,
	`type` VARCHAR(255) NOT NULL,
	`icon` VARCHAR(255) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=84 DEFAULT CHARSET=utf8;


DROP TABLE IF EXISTS `product`;
CREATE TABLE `product` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(255) DEFAULT NULL,
  `subTitle` varchar(255) DEFAULT NULL,
  `originalPrice` float DEFAULT NULL,
  `promotePrice` float DEFAULT NULL,
  `stock` int(11) DEFAULT NULL,
  `cid` int(11) DEFAULT NULL,
  `createDate` datetime DEFAULT NULL,
	`status` int(11) DEFAULT NULL,
	`sales` int(11) DEFAULT 0,
	`reviews` int(11) DEFAULT 0,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=963 DEFAULT CHARSET=utf8;


DROP TABLE IF EXISTS `property`;
CREATE TABLE `property` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `cid` int(11) DEFAULT NULL,
  `name` varchar(255) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=258 DEFAULT CHARSET=utf8;


DROP TABLE IF EXISTS `propertyvalue`;
CREATE TABLE `propertyvalue` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `pid` int(11) DEFAULT NULL,
  `ptid` int(11) DEFAULT NULL,
  `value` varchar(255) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=14092 DEFAULT CHARSET=utf8;


CREATE TABLE `productimage` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `pid` int(11) DEFAULT NULL,
  `type` varchar(255) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=10211 DEFAULT CHARSET=utf8;


DROP TABLE IF EXISTS `advertisement`;
CREATE TABLE `advertisement`(
	`id` int(11) NOT NULL AUTO_INCREMENT,
	`type` VARCHAR(255) DEFAULT NULL, 
	`status_` int(2) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;


DROP TABLE IF EXISTS `carousel`;
CREATE TABLE `carousel`(
	`id` int(11) NOT NULL AUTO_INCREMENT,
	`sid` int(11) DEFAULT NULL,
	`role` int(11) DEFAULT NULL,
	`type` VARCHAR(255) DEFAULT NULL, 
	`status_` int(2) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;


DROP TABLE IF EXISTS `order_`;
CREATE TABLE `order_` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `orderCode` varchar(255) DEFAULT NULL,
  `address` varchar(255) DEFAULT NULL,
  `post` varchar(255) DEFAULT NULL,
  `receiver` varchar(255) DEFAULT NULL,
  `mobile` varchar(255) DEFAULT NULL,
  `userMessage` varchar(255) DEFAULT NULL,
  `createDate` datetime DEFAULT NULL,
  `payDate` datetime DEFAULT NULL,
  `deliveryDate` datetime DEFAULT NULL,
  `confirmDate` datetime DEFAULT NULL,
  `uid` int(11) DEFAULT NULL,
	`express` VARCHAR(255) DEFAULT NULL,
  `status` varchar(255) DEFAULT NULL,
	`sum` DECIMAL DEFAULT NULL,
	`quantity` int(11) DEFAULT NULL,
	`payment` VARCHAR(255) DEFAULT NULL,
	`type` char(2) DEFAULT NULL,
	`sid` int(11) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

DROP TABLE IF EXISTS `logistics`;
CREATE TABLE `logistics` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
	`date` datetime DEFAULT NULL,
	`location` VARCHAR(255) DEFAULT NULL,
	`oid` int(11) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;


DROP TABLE IF EXISTS `orderitem`;
CREATE TABLE `orderitem` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `pid` int(11) DEFAULT NULL,
  `oid` int(11) DEFAULT NULL,
  `uid` int(11) DEFAULT NULL,
	`sid` int(11) DEFAULT NULL,
  `number` int(11) DEFAULT NULL,
	`review` int(2) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

DROP TABLE IF EXISTS `exchange`;
CREATE TABLE `exchange` (
 `id` int(11) NOT NULL AUTO_INCREMENT,
	`oid` int(11) DEFAULT NULL,
	`desc_` MEDIUMTEXT DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

CREATE TABLE `review` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `content` varchar(4000) DEFAULT NULL,
  `uid` int(11) DEFAULT NULL,
  `pid` int(11) DEFAULT NULL,
  `createDate` datetime DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

DROP TABLE IF EXISTS `user`;
CREATE TABLE `user` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(255) DEFAULT NULL,
	`nickName` VARCHAR(255) DEFAULT NULL,
  `password` varchar(255) DEFAULT NULL,
	`salt` VARCHAR(255) DEFAULT NULL,
	`account` FLOAT DEFAULT NULL,
	`sex` int(2) DEFAULT NULL,
	`registerDate` datetime DEFAULT NULL,
	`openDate` datetime DEFAULT NULL,
	`address` VARCHAR(255) DEFAULT NULL,
	`mobile` VARCHAR(255) DEFAULT NULL,
	`email` VARCHAR(255) DEFAULT NULL,
	`mid` int(11) DEFAULT NULL,
	`score` int(11) DEFAULT 0,
	`status` int(2) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;


DROP TABLE IF EXISTS `member`;
CREATE TABLE `member` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `min` int(11) DEFAULT NULL,
  `max` int(11) DEFAULT NULL,
  `name` varchar(255) DEFAULT NULL,
	`score` int(11) DEFAULT NULL,
	`icon` varchar(255) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;



DROP TABLE IF EXISTS `power`;
CREATE TABLE `power` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `mid` int(11) DEFAULT NULL,
  `title` varchar(255) DEFAULT NULL,
	`text` MEDIUMTEXT DEFAULT NULL,
	`score` int(11) DEFAULT NULL,
	`exchange` int(11) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;




DROP TABLE IF EXISTS `manager`;
CREATE TABLE `manager` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
	`sid` int(11) DEFAULT NULL,
  `name` varchar(255) DEFAULT NULL,
  `password` varchar(255) DEFAULT NULL,
	`salt` VARCHAR(255) DEFAULT NULL,
	`nickName` VARCHAR(255) DEFAULT NULL,
	`sex` int(2) DEFAULT NULL,
	`mobile` VARCHAR(255) DEFAULT NULL,
	`email` VARCHAR(255) DEFAULT NULL,
	`createDate` datetime DEFAULT NULL,
	`status` int(2) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;



DROP TABLE IF EXISTS `article`;
CREATE TABLE `article` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `title` VARCHAR(255) DEFAULT NULL,
  `summary` varchar(255) DEFAULT NULL,
	`cid` int(11) DEFAULT NULL,
	`status` int(2) DEFAULT NULL,
	`htmlText` MEDIUMTEXT DEFAULT NULL,
	`createDate` datetime DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;



DROP TABLE IF EXISTS `message`;
CREATE TABLE `message` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
	`uid` int(11) DEFAULT NULL,
	`createDate` datetime DEFAULT NULL,
	`text` MEDIUMTEXT DEFAULT NULL,
	`reply` MEDIUMTEXT DEFAULT NULL,
	`replyDate` datetime DEFAULT NULL,
	`status` int(2) DEFAULT NULL,
	`type` VARCHAR(255) DEFAULT NULL,
	`sid` int(11) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;



DROP TABLE IF EXISTS `option_`;
CREATE TABLE `option_` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` VARCHAR(255) DEFAULT NULL,
	`icon` varchar(255) DEFAULT NULL,
	`key_` varchar(255) DEFAULT NULL,
	`desc_` varchar(255) DEFAULT NULL,
	`right_` varchar(255) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;



CREATE TABLE `log_` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `uid` int(11) DEFAULT NULL,
	`text` varchar(255) DEFAULT NULL,
	`createDate` datetime DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

DROP TABLE IF EXISTS `role`;
create table role (
  `id` INT (11) auto_increment,
  `name` varchar(255),
  `desc_` varchar(255),
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;


create table manager_role (
  `id` INT (11) auto_increment,
  `mid` INT (11),
  `rid` INT (11),
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;


DROP TABLE IF EXISTS `module`;
create table module (
  `id` INT (11) auto_increment,
  `name` varchar(100) DEFAULT NULL,
  `desc_` varchar(100) DEFAULT NULL,
	`pid` INT (11) DEFAULT NULL,
  `url` varchar(100) DEFAULT NULL, 
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;


DROP TABLE IF EXISTS `operation`;
create table operation (
  `id` INT (11) auto_increment,
  `name` varchar(100),
  `desc_` varchar(100),
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;



DROP TABLE IF EXISTS `permission`;
create table permission (
  `id` INT (11) auto_increment,
  `mid` INT (11),
  `oid` INT (11),
  constraint pk_permission primary key(id)
) charset=utf8 ENGINE=InnoDB;


DROP TABLE IF EXISTS `role_permission`;
create table role_permission (
  id INT (11) auto_increment,
  rid INT (11),
  pid INT (11),
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

